// Get yesterday's date range
const yesterday = new Date();
yesterday.setDate(yesterday.getDate() - 1);
const dateStr = yesterday.toISOString().split('T')[0];

const startDate = new Date(dateStr + 'T00:00:00Z');
const endDate = new Date(dateStr + 'T23:59:59Z');

// Get all items from Get Interested Leads node
const allItems = $('Get Interested Leads3').all();

// Extract the actual leads from the nested structure
let allLeads = [];
allItems.forEach(item => {
  if (item.json && item.json.items && Array.isArray(item.json.items)) {
    allLeads = allLeads.concat(item.json.items);
  }
});

// Filter for leads that became interested yesterday
const filteredLeads = allLeads.filter(lead => {
    if (!lead.timestamp_last_interest_change) return false;
    const interestDate = new Date(lead.timestamp_last_interest_change);
    return interestDate >= startDate && interestDate <= endDate;
});

const totalInterested = filteredLeads.length;

return { 
    json: { 
        positive_reply_count: totalInterested
    }
};

// Calculate yesterday's date
const d = new Date();
d.setDate(d.getDate() - 1);
const dateStr = d.toISOString().split('T')[0];
const startDate = new Date(dateStr + 'T00:00:00Z');
const endDate = new Date(dateStr + 'T23:59:59Z');

const campaigns = $input.first().json;
const interestedLeadsData = $input.last().json;

// Handle if campaigns is wrapped in items array
let campaignsArray = [];
if (campaigns.items && Array.isArray(campaigns.items)) {
  campaignsArray = campaigns.items;
} else if (Array.isArray(campaigns)) {
  campaignsArray = campaigns;
} else {
  campaignsArray = [campaigns];
}

// Get all interested leads from the previous node
const allInterestedLeads = interestedLeadsData.leads || [];

// Function to count positive replies for a specific campaign
function getPositiveRepliesForCampaign(campaignId) {
  return allInterestedLeads.filter(lead => lead.campaign === campaignId).length;
}

let totalSent = 0, totalReplied = 0, totalBounced = 0;
let totalPositiveReplies = 0;

campaignsArray.forEach(c => {
  totalSent    += c.emails_sent_count       || 0;
  totalReplied += c.reply_count_unique      || 0;
  totalBounced += c.bounced_count           || 0;
});

totalPositiveReplies = interestedLeadsData.interested_count || 0;

const delivered  = totalSent - totalBounced;
const replyRate  = totalSent > 0 ? ((totalReplied / totalSent) * 100).toFixed(1) : '0.0';
const bounceRate = totalSent > 0 ? ((totalBounced / totalSent) * 100).toFixed(1) : '0.0';

const active = campaignsArray.filter(c => c.campaign_status === 1);
const div = '\u2501'.repeat(38);

let msg = '';
msg += ':bar_chart: *Instantly Daily Analytics Report*\n';
msg += '*Date:* ' + dateStr + '  |  *Campaigns:* ' + active.length + ' active / ' + campaignsArray.length + ' total\n';
msg += div + '\n\n';

msg += ' *Sent*                               ' + totalSent.toLocaleString() + '\n';
msg += ' *Replied*                          ' + totalReplied.toLocaleString() + '  (' + replyRate + '%)\n';
msg += ' *Positive Reply Count*   ' + totalPositiveReplies.toLocaleString() + '\n';
msg += ' *Bounced*                        ' + totalBounced.toLocaleString() + '  (' + bounceRate + '%)\n\n';

msg += div + '\n\n';

if (active.length > 0) {
  msg += ':mega: *Active Campaigns*\n';
  active.forEach(c => {
    const campaignPositiveReplies = getPositiveRepliesForCampaign(c.campaign_id);
    msg += '\u2022 *' + c.campaign_name + '*\n';
    msg += '    Sent: ' + (c.emails_sent_count || 0) + '  |  Replied: ' + (c.reply_count_unique || 0) + '  |  Positive: ' + campaignPositiveReplies + '  |  Bounced: ' + (c.bounced_count || 0) + '\n';
  });
  msg += '\n';
}

msg += ':bar_chart: <https://app.instantly.ai|View Full Dashboard>';

return { json: { text: msg } };
