const today = new Date();
const month = String(today.getMonth() + 1).padStart(2, '0');
const day = String(today.getDate()).padStart(2, '0');
const year = today.getFullYear();
const companyName = $input.item.json.body.companyName;
const dateFolder = `${companyName} ${month}-${day}-${year}`;

return {
  dateFolderName: dateFolder,
  companyFolderId: $('Set Company Folder ID').item.json.companyFolderId,
  rootFolderId: $('Set Root Folder ID').item.json.rootFolderId,
  internalFolderName: $input.item.json.body.internalFolderName,
  companyName: companyName,
  foldersCreated: $('Set Company Folder ID').item.json.foldersCreated || []
};

const markdown = $input.item.json.response;
const companyName = $('Prepare Date Folder Name').item.json.companyName;

// Simple markdown to HTML conversion
let html = markdown
  .replace(/^### (.*$)/gim, '<h3>$1</h3>')
  .replace(/^## (.*$)/gim, '<h2>$1</h2>')
  .replace(/^# (.*$)/gim, '<h1>$1</h1>')
  .replace(/\*\*(.+?)\*\*/g, '<strong>$1</strong>')
  .replace(/\*(.+?)\*/g, '<em>$1</em>')
  .replace(/^\* (.+)$/gim, '<li>$1</li>')
  .replace(/^- (.+)$/gim, '<li>$1</li>')
  .replace(/\n\n/g, '</p><p>')
  .replace(/<li>/g, '<ul><li>')
  .replace(/<\/li>\n(?!<li>)/g, '</li></ul>');

html = `<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <title>${companyName} - Company Research</title>
  <style>
    body { font-family: Arial, sans-serif; line-height: 1.6; padding: 20px; max-width: 800px; margin: 0 auto; }
    h1 { color: #2c3e50; border-bottom: 2px solid #3498db; padding-bottom: 10px; }
    h2 { color: #34495e; margin-top: 20px; }
    h3 { color: #7f8c8d; }
    ul { margin: 10px 0; padding-left: 30px; }
  </style>
</head>
<body>
  <h1>${companyName} - Company Research</h1>
  <p>${html}</p>
</body>
</html>`;

return {
  html: html,
  internalFolderId: $('Set Internal Folder ID').item.json.internalFolderId,
  companyName: companyName,
  foldersCreated: $('Set Internal Folder ID').item.json.foldersCreated
};

const foldersCreated = $input.item.json.foldersCreated || [];
const companyName = $input.item.json.companyName;
const docName = `${companyName} - Research Report`;

let subject = '';
let body = '';

if (foldersCreated.length === 0) {
  subject = `[n8n] Document uploaded - All folders existed for ${companyName}`;
  body = `<p>The research document "${docName}" has been successfully uploaded.</p>
         <p><strong>Folder Status:</strong> All required folders already existed.</p>`;
} else if (foldersCreated.length === 1) {
  subject = `[n8n] Document uploaded - Created folder: ${foldersCreated[0]}`;
  body = `<p>The research document "${docName}" has been successfully uploaded.</p>
         <p><strong>Folder Created:</strong> ${foldersCreated[0]}</p>`;
} else {
  subject = `[n8n] Document uploaded - Created ${foldersCreated.length} folders for ${companyName}`;
  body = `<p>The research document "${docName}" has been successfully uploaded.</p>
         <p><strong>Folders Created:</strong></p><ul>${foldersCreated.map(f => '<li>' + f + '</li>').join('')}</ul>`;
}

body += `<p><strong>Document Location:</strong> n8n Demo > ${companyName} > [Date Folder] > [Internal Folder]</p>
        <p>Workflow completed successfully at ${new Date().toLocaleString()}.</p>`;

return {
  subject: subject,
  body: body,
  webLink: $input.item.json.webViewLink
};

const rootFolderId = $input.item.json.files && $input.item.json.files.length > 0 
  ? $input.item.json.files[0].id 
  : $input.item.json.id;

return {
  rootFolderId: rootFolderId,
  foldersCreated: $input.item.json.name ? ['n8n Demo'] : []
};

const companyFolderId = $input.item.json.files && $input.item.json.files.length > 0 
  ? $input.item.json.files[0].id 
  : $input.item.json.id;

const previousFolders = $('Set Root Folder ID').item.json.foldersCreated || [];
const newFolders = $input.item.json.name ? [...previousFolders, $input.item.json.name] : previousFolders;

return {
  companyFolderId: companyFolderId,
  foldersCreated: newFolders
};

