// Extract subject line from the formatted email
const emailContent = $input.first().json.message.content;

// Find subject line
const subjectMatch = emailContent.match(/Subject:\s*(.+)/i);
const subject = subjectMatch ? subjectMatch[1].trim() : 'Progress Update';

// Get email body (everything after Subject line)
const bodyStart = emailContent.indexOf('Dear');
const body = bodyStart !== -1 ? emailContent.substring(bodyStart) : emailContent;

return {
  subject: subject,
  body: body,
  fullEmail: emailContent
};
