const now = new Date();
const yesterday = new Date(now);
yesterday.setDate(yesterday.getDate() - 1);
const yesterdayStr = yesterday.toISOString().split('T')[0];
const todayStr = now.toISOString().split('T')[0];
const allTimeStart = '2024-01-01';

return [{
  json: {
    yesterday: yesterdayStr,
    today: todayStr,
    allTimeStart: allTimeStart
  }
}];

const items = $input.all();
let allCampaigns = [];

for (const item of items) {
  if (item.json.items && Array.isArray(item.json.items)) {
    allCampaigns.push(...item.json.items);
  } else if (item.json.id) {
    allCampaigns.push(item.json);
  }
}

const companyCampaigns = [];
const peopleCampaigns = [];

for (const campaign of allCampaigns) {
  const name = (campaign.name || '').trim();
  const nameLower = name.toLowerCase();
  if (nameLower.startsWith('companies | email')) {
    companyCampaigns.push({ id: campaign.id, name: name });
  } else if (nameLower.startsWith('personal | email')) {
    peopleCampaigns.push({ id: campaign.id, name: name });
  }
}

console.log(`Found ${companyCampaigns.length} company campaigns, ${peopleCampaigns.length} personal campaigns`);

return [{
  json: {
    companyCampaignIds: companyCampaigns.map(c => c.id),
    peopleCampaignIds: peopleCampaigns.map(c => c.id),
    companyCampaigns,
    peopleCampaigns,
    totalCampaigns: allCampaigns.length
  }
}];

const campaignData = $node['Categorize Campaigns1'].json;
const dateVars = $node['Set Date Variables1'].json;
const dailyAnalyticsItems = $input.all();
const API_KEY = "YjI4NWQ4ZGItY2VkZi00YTNiLTg2NTUtOTJiMTI1YzBmMDhmOmNGdGhMU3dvSGx3TQ==";

const companyCampaignIds = campaignData.companyCampaignIds || [];
const peopleCampaignIds = campaignData.peopleCampaignIds || [];
const yesterday = dateVars.yesterday;

const FREE_DOMAINS = new Set([
  'gmail.com', 'yahoo.com', 'hotmail.com', 'outlook.com',
  'aol.com', 'icloud.com', 'mail.com', 'protonmail.com',
  'zoho.com', 'yandex.com', 'gmx.com', 'live.com',
  'msn.com', 'me.com', 'comcast.net', 'att.net',
  'verizon.net', 'cox.net', 'sbcglobal.net'
]);

const yesterdayStart = new Date(yesterday + 'T00:00:00Z');
const yesterdayEnd = new Date(yesterday + 'T23:59:59.999Z');

function wasContactedOnDate(lead) {
  const ts = lead.timestamp_last_step_executed || lead.last_step_timestamp_executed;
  if (!ts) return false;
  const d = new Date(ts);
  return d >= yesterdayStart && d <= yesterdayEnd;
}

function getDomain(lead) {
  let domain = (lead.company_domain || '').toLowerCase().trim();
  if (!domain || FREE_DOMAINS.has(domain)) {
    const email = (lead.email || '').toLowerCase();
    const parts = email.split('@');
    if (parts.length === 2) domain = parts[1];
  }
  if (FREE_DOMAINS.has(domain)) return null;
  return domain || null;
}

function getName(lead) {
  const first = (lead.first_name || '').trim().toLowerCase();
  const last = (lead.last_name || '').trim().toLowerCase();
  const name = `${first} ${last}`.trim();
  return name && name.length > 1 ? name : null;
}

async function fetchLeadsForCampaign(campaignId) {
  const allLeads = [];
  let cursor = null;
  let hasMore = true;
  let page = 0;

  while (hasMore && page < 100) {
    const body = { campaign_id: campaignId, limit: 100 };
    if (cursor) body.starting_after = cursor;

    try {
      const response = await fetch('https://api.instantly.ai/api/v2/leads/list', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${API_KEY}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(body)
      });

      if (!response.ok) throw new Error(`HTTP ${response.status}`);

      const data = await response.json();
      const leads = data.items || [];
      if (Array.isArray(leads)) allLeads.push(...leads);

      cursor = data.next_starting_after;
      hasMore = !!cursor;
      page++;

      if (hasMore) await new Promise(r => setTimeout(r, 250));
    } catch (error) {
      console.log(`Error fetching campaign ${campaignId}: ${error.message}`);
      hasMore = false;
    }
  }
  return allLeads;
}

const companyLeads = [];
for (const cid of companyCampaignIds) {
  companyLeads.push(...(await fetchLeadsForCampaign(cid)));
}

const peopleLeads = [];
for (const cid of peopleCampaignIds) {
  peopleLeads.push(...(await fetchLeadsForCampaign(cid)));
}

const companyContactedYesterday = companyLeads.filter(wasContactedOnDate);
const peopleContactedYesterday = peopleLeads.filter(wasContactedOnDate);

const uniqueDomains = new Set();
for (const lead of companyContactedYesterday) {
  const domain = getDomain(lead);
  if (domain) uniqueDomains.add(domain);
}

const uniqueNames = new Set();
for (const lead of peopleContactedYesterday) {
  const name = getName(lead);
  if (name) uniqueNames.add(name);
}

// Process analytics data from native Instantly node
let dailyReplies = 0;
let dailySent = 0;

// Get all campaign analytics items
const allCampaignIds = [...companyCampaignIds, ...peopleCampaignIds];
const campaignIdSet = new Set(allCampaignIds);

// Sum up metrics from matching campaigns
for (const item of dailyAnalyticsItems) {
  const campaign = item.json;
  if (campaign && campaign.campaign_id && campaignIdSet.has(campaign.campaign_id)) {
    // Use reply_count from the native node response
    dailyReplies += (campaign.reply_count || campaign.reply_count_unique || 0);
    // Use emails_sent_count or new_leads_contacted_count
    dailySent += (campaign.emails_sent_count || campaign.new_leads_contacted_count || 0);
  }
}

const allContactedYesterday = [...companyContactedYesterday, ...peopleContactedYesterday];
const dailyPositiveReplies = allContactedYesterday.filter(l =>
  (l.interest_status === 1 || l.lt_interest_status === 1) &&
  (l.email_reply_count > 0 || l.reply_count > 0)
).length;

return [{
  json: {
    daily: {
      uniqueCompanies: uniqueDomains.size,
      uniquePeople: uniqueNames.size,
      totalUniqueContacts: uniqueDomains.size + uniqueNames.size,
      replies: dailyReplies,
      positiveReplies: dailyPositiveReplies,
      sent: dailySent,
      date: yesterday
    },
    _meta: {
      companyLeadsTotal: companyLeads.length,
      peopleLeadsTotal: peopleLeads.length,
      companyContactedYesterday: companyContactedYesterday.length,
      peopleContactedYesterday: peopleContactedYesterday.length
    }
  }
}];

