// Date ranges
const yesterdayStart = new Date(yesterday + 'T00:00:00Z');
const yesterdayEnd = new Date(yesterday + 'T23:59:59.999Z');

// Helper functions
function wasContactedOnDate(lead, date) {
  const ts = lead.timestamp_last_contact;
  if (!ts) return false;
  const d = new Date(ts);
  const dateStart = new Date(date + 'T00:00:00Z');
  const dateEnd = new Date(date + 'T23:59:59.999Z');
  return d >= dateStart && d <= dateEnd;
}

function getEmail(lead) {
  return (lead.email || '').toLowerCase().trim();
}

function getCampaignStartDate(campaigns) {
  let earliestDate = null;
  for (const campaign of campaigns) {
    const created = campaign.created_at;
    if (created) {
      const dateStr = new Date(created).toISOString().split('T')[0];
      if (!earliestDate || dateStr < earliestDate) {
        earliestDate = dateStr;
      }
    }
  }
  return earliestDate || 'N/A';
}

// Separate leads by campaign type
const companyLeads = [];
const peopleLeads = [];

for (const item of allLeads) {
  const lead = item.json;
  const campaignId = lead.campaign; // This is the correct field name

  if (!campaignId) {
    continue; // Skip if no campaign ID found
  }

  if (companyCampaignIds.has(campaignId)) {
    companyLeads.push(lead);
  } else if (peopleCampaignIds.has(campaignId)) {
    peopleLeads.push(lead);
  }
}

