        uniqueLeadsContacted: companyEmailsYesterday.size,
        date: yesterday
      },
      peopleCampaigns: {
        uniqueLeadsContacted: peopleEmailsYesterday.size,
        date: yesterday
      },
      total: companyEmailsYesterday.size + peopleEmailsYesterday.size
    },
    allTime: {
      companyCampaigns: {
        uniqueLeadsContacted: companyEmailsAllTime.size,
        startDate: companyStartDate
      },
