yesterday.setDate(yesterday.getDate() - 1);
const yesterdayStr = yesterday.toISOString().split('T')[0];
const todayStr = now.toISOString().split('T')[0];

return [{
  json: {
    yesterday: yesterdayStr,
    today: todayStr
  }
}];

const items = $input.all();
let allCampaigns = [];

for (const item of items) {
  if (item.json.items && Array.isArray(item.json.items)) {
    allCampaigns.push(...item.json.items);
  } else if (item.json.id) {
    allCampaigns.push(item.json);
  }
}

const companyCampaigns = [];
const peopleCampaigns = [];

for (const campaign of allCampaigns) {
  const name = (campaign.name || '').trim();
  const nameLower = name.toLowerCase();
  if (nameLower.startsWith('companies | email')) {
    companyCampaigns.push({ 
      id: campaign.id, 
      name: name,
      created_at: campaign.created_at || campaign.createdAt
    });
  } else if (nameLower.startsWith('personal | email')) {
    peopleCampaigns.push({ 
      id: campaign.id, 
      name: name,
      created_at: campaign.created_at || campaign.createdAt
    });
  }
}

console.log(`Found ${companyCampaigns.length} company campaigns, ${peopleCampaigns.length} personal campaigns`);

return [{
  json: {
    companyCampaignIds: companyCampaigns.map(c => c.id),
    peopleCampaignIds: peopleCampaigns.map(c => c.id),
    companyCampaigns,
    peopleCampaigns,
    totalCampaigns: allCampaigns.length
  }
}];

const campaignData = $input.first().json;

const companyCampaignIds = campaignData.companyCampaignIds || [];
const peopleCampaignIds = campaignData.peopleCampaignIds || [];

const items = [];

// Create items for company campaigns
for (const campaignId of companyCampaignIds) {
  items.push({
    json: {
      campaign_id: campaignId,
      campaignType: 'Company',
      skip: 0,
      limit: 100,
      filter: "FILTER_VAL_CONTACTED",
      allLeads: [] // Will accumulate leads across pagination
    }
  });
}

// Create items for people campaigns
for (const campaignId of peopleCampaignIds) {
  items.push({
    json: {
      campaign_id: campaignId,
      campaignType: 'People',
      skip: 0,
      limit: 100,
      filter: "FILTER_VAL_CONTACTED",
      allLeads: [] // Will accumulate leads across pagination
    }
  });
}

console.log(`Created ${items.length} campaign items for processing`);

return items;

// Get current item from loop
const currentItem = $input.first().json;

// Get the response from API
const apiResponse = currentItem;

// Get leads from response (adjust based on actual API response structure)
const newLeads = apiResponse.leads || apiResponse.data || apiResponse.items || [];

// Get accumulated data from context
const campaign_id = currentItem.campaign_id || $('Loop Over Campaigns').item.json.campaign_id;
const campaignType = currentItem.campaignType || $('Loop Over Campaigns').item.json.campaignType;
const currentSkip = $('Loop Over Campaigns').item.json.skip;
const allLeads = $('Loop Over Campaigns').item.json.allLeads || [];

// Add new leads to accumulated leads
const updatedLeads = [...allLeads, ...newLeads];

console.log(`Campaign ${campaign_id}: Fetched ${newLeads.length} leads at skip ${currentSkip}. Total so far: ${updatedLeads.length}`);

// Determine if we need to continue pagination
const hasMoreLeads = newLeads.length === 100; // If we got full batch, there might be more

return [{
  json: {
    campaign_id: campaign_id,
    campaignType: campaignType,
    skip: currentSkip + 100, // Increment for next batch
    limit: 100,
    filter: "FILTER_VAL_CONTACTED",
    allLeads: updatedLeads,
    currentBatchSize: newLeads.length,
    hasMoreLeads: hasMoreLeads,
    totalLeadsSoFar: updatedLeads.length
  }
}];

// Get all items (each representing a campaign with all its leads)
const items = $input.all();

const companyCampaignLeads = [];
const peopleCampaignLeads = [];
let totalLeadsCount = 0;

for (const item of items) {
  const campaignId = item.json.campaign_id;
  const campaignType = item.json.campaignType;
  const leads = item.json.allLeads || [];

  totalLeadsCount += leads.length;

  if (campaignType === 'Company') {
    companyCampaignLeads.push({
      campaign_id: campaignId,
      campaignType: campaignType,
      leadCount: leads.length,
      leads: leads
    });
  } else if (campaignType === 'People') {
    peopleCampaignLeads.push({
      campaign_id: campaignId,
      campaignType: campaignType,
      leadCount: leads.length,
      leads: leads
    });
  }
}

console.log(`Total leads fetched: ${totalLeadsCount}`);
console.log(`Company campaigns: ${companyCampaignLeads.length} campaigns with ${companyCampaignLeads.reduce((sum, c) => sum + c.leadCount, 0)} leads`);
console.log(`People campaigns: ${peopleCampaignLeads.length} campaigns with ${peopleCampaignLeads.reduce((sum, c) => sum + c.leadCount, 0)} leads`);

return [{
  json: {
    companyCampaignLeads: companyCampaignLeads,
    peopleCampaignLeads: peopleCampaignLeads,
    totalLeads: totalLeadsCount,
    totalCompanyCampaigns: companyCampaignLeads.length,
    totalPeopleCampaigns: peopleCampaignLeads.length,
    summary: {
      companyLeadsCount: companyCampaignLeads.reduce((sum, c) => sum + c.leadCount, 0),
      peopleLeadsCount: peopleCampaignLeads.reduce((sum, c) => sum + c.leadCount, 0)
    }
  }
}];

