  } else if (name.toLowerCase().startsWith('people')) {
    peopleCampaigns.push({ id: campaign.id, name: name });
  }
}

console.log(`Found ${companyCampaigns.length} company campaigns, ${peopleCampaigns.length} people campaigns`);

return [{
  json: {
    companyCampaignIds: companyCampaigns.map(c => c.id),
    peopleCampaignIds: peopleCampaigns.map(c => c.id),
    companyCampaigns,
    peopleCampaigns,
    totalCampaigns: allCampaigns.length
  }
}];

