const companyCampaignIds = campaignData.companyCampaignIds || [];
const peopleCampaignIds = campaignData.peopleCampaignIds || [];
const yesterday = dateVars.yesterday;

const FREE_DOMAINS = new Set([
  'gmail.com', 'yahoo.com', 'hotmail.com', 'outlook.com',
  'aol.com', 'icloud.com', 'mail.com', 'protonmail.com',
  'zoho.com', 'yandex.com', 'gmx.com', 'live.com',
  'msn.com', 'me.com', 'comcast.net', 'att.net',
  'verizon.net', 'cox.net', 'sbcglobal.net'
]);

const yesterdayStart = new Date(yesterday + 'T00:00:00Z');
const yesterdayEnd = new Date(yesterday + 'T23:59:59.999Z');

function wasContactedOnDate(lead) {
  const ts = lead.timestamp_last_step_executed || lead.last_step_timestamp_executed;
  if (!ts) return false;
  const d = new Date(ts);
  return d >= yesterdayStart && d <= yesterdayEnd;
}

function getDomain(lead) {
  let domain = (lead.company_domain || '').toLowerCase().trim();
  if (!domain || FREE_DOMAINS.has(domain)) {
    const email = (lead.email || '').toLowerCase();
    const parts = email.split('@');
    if (parts.length === 2) domain = parts[1];
  }
  if (FREE_DOMAINS.has(domain)) return null;
  return domain || null;
}

function getName(lead) {
  const first = (lead.first_name || '').trim().toLowerCase();
  const last = (lead.last_name || '').trim().toLowerCase();
  const name = `${first} ${last}`.trim();
  return name && name.length > 1 ? name : null;
}

async function fetchLeadsForCampaign(campaignId) {
  const allLeads = [];
  let cursor = null;
  let hasMore = true;
  let page = 0;

  while (hasMore && page < 100) {
    const body = { campaign_id: campaignId, limit: 100 };
    if (cursor) body.starting_after = cursor;

    try {
      const response = await fetch('https://api.instantly.ai/api/v2/leads/list', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${API_KEY}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(body)
      });

      if (!response.ok) throw new Error(`HTTP ${response.status}`);

      const data = await response.json();
      const leads = data.items || [];
      if (Array.isArray(leads)) allLeads.push(...leads);

      cursor = data.next_starting_after;
      hasMore = !!cursor;
      page++;

      if (hasMore) await new Promise(r => setTimeout(r, 250));
    } catch (error) {
      console.log(`Error fetching campaign ${campaignId}: ${error.message}`);
      hasMore = false;
    }
  }
  return allLeads;
}

