};
const allTime = input1.allTime || input2.allTime || {
  uniqueCompanies: 0, uniquePeople: 0, totalUniqueContacts: 0,
  replies: 0, positiveReplies: 0, sent: 0
};

const dailyPositiveRate = daily.replies > 0
  ? ((daily.positiveReplies / daily.replies) * 100).toFixed(1)
  : '0.0';

const allTimePositiveRate = allTime.replies > 0
  ? ((allTime.positiveReplies / allTime.replies) * 100).toFixed(1)
  : '0.0';

const fmt = (n) => (n || 0).toLocaleString('en-US');

const messageText = [
  `*Outbound Report - ${daily.date}*`,
  '',
  '*:calendar: Yesterday*',
  `> *Unique Companies:* ${fmt(daily.uniqueCompanies)}    |    *Unique People:* ${fmt(daily.uniquePeople)}`,
  `> *Total Contacts:* ${fmt(daily.totalUniqueContacts)}    |    *Emails Sent:* ${fmt(daily.sent)}`,
  `> *Replies:* ${fmt(daily.replies)}    |    *Positive Replies:* ${fmt(daily.positiveReplies)} (${dailyPositiveRate}%)`,
  '',
  '*:chart_with_upwards_trend: All-Time*',
  `> *Unique Companies:* ${fmt(allTime.uniqueCompanies)}    |    *Unique People:* ${fmt(allTime.uniquePeople)}`,
  `> *Total Contacts:* ${fmt(allTime.totalUniqueContacts)}    |    *Emails Sent:* ${fmt(allTime.sent)}`,
  `> *Replies:* ${fmt(allTime.replies)}    |    *Positive Replies:* ${fmt(allTime.positiveReplies)} (${allTimePositiveRate}%)`,
  '',
  `_Report generated at ${new Date().toLocaleString('en-US', { timeZone: 'America/New_York' })} ET_`
].join('\n');

return [{ json: { messageText } }];
